mapper.select:
select
id, p_name, age, gender, email, phone, title
from pzh_meeting_management.person


        and id = #{id}


        and p_name = #{pName}


        and age = #{age}


        and gender = #{gender}


        and email = #{email}


        and phone = #{phone}


        and title = #{title}

mapper.update:
update pzh_meeting_management.activity_person


        conference_id = #{conference_id},


        person_id = #{person_id}


    where id = #{id}

mapper.select:
select id
from pzh_meeting_management.activity_person
where person_id = #{person_id}

select conference_id
from pzh_meeting_management.activity_person
where person_id = #{person_id}
