mapper.select:
select
id, s_name, gender, email, phone, position, conference_id
from pzh_meeting_management.staff


        and id = #{id}


        and s_name = #{sName}


        and gender = #{gender}


        and email = #{email}


        and phone = #{phone}


        and position = #{position}


        and conference_id = #{conferenceId}

select id
from pzh_meeting_management.staff
where conference_id = #{conferenceId}
