mapper.select:
select
id, sugg_time, content, person_id, confer_id, suggestion_title
from pzh_meeting_management.suggestion


        and id = #{id}


        and sugg_time = #{suggTime}


        and content = #{content}


        and person_id = #{personId}


        and confer_id = #{conferId}


        and suggestion_title = #{suggestionTitle}
